/**
 * Interpolate a given template string by filling the placeholders with the params.
 *
 * Placeholder syntax:
 *  {{name}}
 *
 * @example
 * interpolate('<div>{{text}}</div>', {text: 'Hello World!'})
 *  => '<div>Hello World!</div>'
 *
 * @param {string} template
 * @param {*} [params={}]
 * @returns {string}
 */
export declare function interpolate(template: string, params?: any): string;
